mapper.update:
update sm_user
set user_picture = #{user_picture}
where user_id = #{user_id}

mapper.select:
select user_email, user_name, user_picture, reg_date
from sm_user
where user_id = #{user_id}

select user_friend
from sm_user
where user_id = #{user_id}

mapper.update:
update sm_user
set user_friend = #{user_friend}
where user_id = #{user_id}

mapper.select:
select count(user_id)
from sm_user
where access_id = #{access_id}
