mapper.select:
select memo_id, memo_name, reg_date, update_date
from sm_memo
where memo_id = #{memo_id}

mapper.update:
update sm_memo
set memo_name = #{memo_name}
where memo_id = #{memo_id}

update sm_memo
set memo_group = #{memo_group}
where memo_id = #{memo_id}

mapper.select:
select memo_group
from sm_memo
where memo_id = #{memo_id}
